function taxToggle(details, switcher, expandedClassName) {
  if ($(details).style.display == 'none') {
    $(details).show();
    $(switcher).addClassName(expandedClassName);
  } else {
    $(details).hide();
    $(switcher).removeClassName(expandedClassName);
  }
}

